    setearIndex: function() {
        var arg = arguments;
        var sec_ = arg[0];
        /*{begin-client-desktop}*/
        var index = sec_.getIndex();
        /*{end-client-desktop}*/
        var sec_act;
        for (var i = 0; i < sec_.size(); i++) {
            sec_act = sec_.get(i);
            sec_act.setIndex_(i + 1);
            sec_act.setActiva(1);
        }
        /*{begin-client-desktop}*/
        sec_.get(index);
        /*{end-client-desktop}*/
    },
    setearIndexIncisoDesc: function() {
        var arg = arguments;
        var sec_ = arg[0];
        var qy = arg[1];
        /*{begin-client-desktop}*/
        var index = sec_.getIndex();
        /*{end-client-desktop}*/
        var sec_act;
        for (var i = 0; i < sec_.size(); i++) {
            var inciso = '';
            var descripcion = '';
            if (qy != null) {
                if (qy.get(i) != null) {
                    var v_qy = qy.get(i);
                    inciso = v_qy.getInciso();
                    descripcion = v_qy.getDescripcion();
                }
            }
            sec_act = sec_.get(i);
            sec_act.setIndex_(i + 1);
            sec_act.setActiva(1);
            sec_act.setInciso(inciso);
            sec_act.setDescripcion(descripcion);
        }
        /*{begin-client-desktop}*/
        sec_.get(index);
        /*{end-client-desktop}*/
    },
    activaSeccion: function() {
        var arg = arguments;
        var ent = arg[0];
        var sec_ = arg[1];
        var qy = arg[2];
        var pregunta = arg[3];
        var campos = arg[4];
        /*{begin-client-desktop}*/
        var index = sec_.getIndex();
        /*{end-client-desktop}*/
        var varPreg = '';
        var esp = '';
        var sec_act;
        for (var i = 0; i < sec_.size(); i++) {
            if (i < 9) {
                varPreg = pregunta + '_0' + (i + 1);
            } else {
                varPreg = pregunta + '_' + (i + 1);
            }
            sec_act = sec_.get(i);
            var inciso = '';
            var descripcion = '';
            sec_act.setIndex_(i + 1);
            if (qy != null) {
                if (qy.get(i) != null) {
                    var v_qy = qy.get(i);
                    inciso = v_qy.getInciso();
                    descripcion = v_qy.getDescripcion();
                }
            }
            var valPreg = ent[com_funciones.regresaGet(varPreg)]();
            if (valPreg != 1) {
                sec_act.setActiva(0);
                for (var j = 0; j < campos.length; j++) {
                    sec_act[com_funciones.regresaSet(campos[j])]('');
                }
            } else {
                sec_act.setActiva(1);
            }
            sec_act[com_funciones.regresaSet(pregunta)](valPreg);
            sec_act.setInciso(inciso);
            sec_act.setDescripcion(descripcion);
        }
        /*{begin-client-desktop}*/
        sec_.get(index);
        /*{end-client-desktop}*/
    },
    activaSeccion2: function() {
