            var descripcion = '';
            sec_act.setIndex_(i + 1);
            if (qy != null) {
                if (qy.get(i) != null) {
                    var v_qy = qy.get(i);
                    inciso = v_qy.getInciso();
                    descripcion = v_qy.getDescripcion();
                    if (inciso * 1 == 6) {
                        varPreg = pregunta + '_' + inciso + '_0' + cont_esp_06;
                        cont_esp_06++;
                    } else if (inciso * 1 == 15) {
                        varPreg = pregunta + '_' + inciso + '_0' + cont_esp_15;
                        cont_esp_15++;
                    } else {
                        varPreg = pregunta + '_' + inciso;
                    }
                }
            }
            var valPreg = ent[com_funciones.regresaGet(varPreg)]();
            if (valPreg != 1) {
                sec_act.setActiva(0);
                sec_act.setDescripcion(descripcion);
                for (var j = 0; j < campos.length; j++) {
                    sec_act[com_funciones.regresaSet(campos[j])]('');
                }
            } else {
                sec_act.setActiva(1);
                var valPreg_esp = '';
                if (inciso * 1 == 6 || inciso * 1 == 15) {
                    valPreg_esp = ent[com_funciones.regresaGet(varPreg + 'e')]();
                    sec_act.setDescripcion(valPreg_esp);
                } else {
                    sec_act.setDescripcion(descripcion);
                }
            }
            sec_act[com_funciones.regresaSet(pregunta)](valPreg);
            sec_act.setInciso(inciso);
        }
        /*{begin-client-desktop}*/
        sec_.get(index);
        /*{end-client-desktop}*/
    }
