var com_funciones = {
    regresaSet: function() {
        var arg = arguments;
        if (arg.length >= 1) {
            var val = arg[0];
            if (val.length > 1) {
                var valUpper = val.substring(0, 1).toUpperCase();
                var valDown = val.substring(1, val.length);
                return 'set' + valUpper + valDown;
            }
        }
    },
    regresaGet: function() {
        var arg = arguments;
        if (arg.length >= 1) {
            var val = arg[0];
            if (val.length > 1) {
                var valUpper = val.substring(0, 1).toUpperCase();
                var valDown = val.substring(1, val.length);
                return 'get' + valUpper + valDown;
            }
        }
    },
    regresaActivaGrupo: function(strActiva, strGrupo) {
        var indx = strActiva.indexOf(strGrupo);
        if (indx > -1) {
            return true;
        }
        return false;
    },
    agregaActivaGrupo: function(ent, strActiva, strGrupo) {
        var valor = ent[com_funciones.regresaGet(strActiva)]();
        //if (valor.length > 0) {
        var indx = valor.indexOf(strGrupo);
        if (indx == -1) {
            ent[com_funciones.regresaSet(strActiva)](valor + ',' + strGrupo);
        }
    },
    saltoPorRango: function(ent, campo, inicio, fin, valorTrue){      
      // tr_envipe2018_eleg.getAp4_11_01()
      var total= 0;
      var stringInicio= '';
      var numberInicio= 0;
      var finalInicio= '';
      var valorGet= '';
      for (var x= inicio; x<= fin; x++){
        stringInicio= x.toString();
          numberInicio= stringInicio.length;      
        if (numberInicio== 2){
          finalInicio= stringInicio;    
        } // if        
        else{
          finalInicio= '0'+ stringInicio;    
        } // else       
        valorGet= ent[com_funciones.regresaGet(campo+ finalInicio)]();
        if (valorGet== valorTrue){
          total= total+ 1;			
	    } // if
	  } // for x
      if (total> 0){
        return true;      
      } // if
      else{
        return false;  
      } // else
	} // saltoPorRango
};
