var CUESTIONARIO = CUESTIONARIO || {};

CUESTIONARIO.General = {
    /**
     * Formatea la palabra ingresada de la forma: primer letra en mayúscula y el resto en minúsculas.
     * @param name    Palabra para dar formato.
     * @return       Palabra con la primer letra en mayúscula y el resto en minúscula.
     * */
    camelCase: function(name) {
        var valUpper = name.substring(0, 1).toUpperCase(),
            valDown = name.substring(1, name.length);
        return valUpper + valDown;
    },
    /**
     * Formatea el nombre del campo para un 'Set'. Ejemplo: setVariable.
     * @param name	Nombre del campo. Ejemplo: un_campo.
     * @return       Nombre del campo con la palabra set al inicio. Ejemplo: setUn_campo.
     * */
    regresaSet: function(name) {
        return 'set' + this.camelCase(name);
    },
    /**
     * Formatea el nombre del campo para un 'Get'. Ejemplo: getVariable.
     * @param name	Nombre del campo. Ejemplo: un_campo.
     * @return       Nombre del campo con la palabra get al inicio. Ejemplo: getUn_campo.
     * */
    regresaGet: function(name) {
        return 'get' + this.camelCase(name);
    },
    /**
     * Limpia los checks excluyentes.
     * Ejemplo: excluyente(tr_cuestionario, 'p4_28_', 13)
     *          excluyente(tr_cuestionario, 'p4_28_', 13, ['campo_1_98', 'campo_2_35'])
     * @param entity       Entidad donde se encuentran los checks.
     * @param fieldName    Nombre del campo de la forma: 'campo_1_'. Cuando en los primeros 9 números
     *                     el consecutivo sea 01,02..09 se enviará de la sig. manera: 'check_0'.
	 * @param totCheck     Total de checks consecutivos.
     * @param extraFields  Los campos no consecutivos. Ejemplo: ['campo_1_98', 'campo_2_35']
     **/
    excluyente: function(entity, fieldName, totCheck, extraFields) {
        var setCampo,
            len = fieldName.length,
            ponCero = (fieldName.substring(len - 1, len)) == '0',
            campo = ponCero ? (fieldName.substring(0, len - 1)) : fieldName;
        if (campo != null && campo != '') {
            for (var i = 1; i <= totCheck; i++) {
                if (i < 10) {
                    setCampo = campo + (ponCero ? '0' + i : i);
                } else {
                    setCampo = campo + i;
                }
                entity[CUESTIONARIO.General.regresaSet(setCampo)]('');
            }
        }
		if (extraFields != null) {
            for (var j = 0; j < extraFields.length; j++) {
                setCampo = extraFields[j];
                entity[CUESTIONARIO.General.regresaSet(setCampo)]('');
            }
        }

    },
    /**
     * Cuenta los checks seleccionados. Si hay checks fuera de rango se pondrán al final
	 * de los parámetros. Ejemplo:
	 * countChSelected(tr_cuestionario, 'fieldName_', 5)) para validar de 1 a 5
	 * countChSelected(tr_cuestionario, 'fieldName_', 5, 8, 9...)) para validar del 1 a 5 y 8 y 9, etc.
     * @param entity     Nombre de la entidad donde se encuentra el check.
     * @param fieldName  Nombre del campo de la forma: 'campo_1_'. Cuando en los primeros 9 números
     *                   el consecutivo sea 01,02..09 se enviará de la sig. manera: 'check_0'.
     * @param finRange	 Fin del rango.
     * @return           Total de checks seleccionados.
     * */
    countChSelected: function(entity, fieldName, finRange) {
        var totalParametros = arguments.length; //Total de parametros enviados
        var arg = arguments,
            ent = entity,
            getCampo = fieldName,
            ranFin = finRange * 1,
            len = fieldName.length,
            ponCero = (fieldName.substring(len - 1, len)) == '0',
            campo = ponCero ? (fieldName.substring(0, len - 1)) : fieldName,
            res = 0;
        for (var i = 1; i <= ranFin; i++) {
            getCampo = (i < 10 && ponCero) ? campo + '0' + i : campo + i;
            res += (ent[CUESTIONARIO.General.regresaGet(getCampo)]() == 1) ? 1 : 0;
        }
        for (var j = 3; j < totalParametros; j++) {
            getCampo = campo + arg[j];
            res += (ent[CUESTIONARIO.General.regresaGet(getCampo)]() == 1) ? 1 : 0;
        }
        return res;
    }
};

CUESTIONARIO.Validacion = {
    /**
     * Verifica que el texto sea una palabra válida.
     * @param texto    Texto a validar.
     * @return         True cuando es un texto válido.
     **/
    palabraCorrecta: function(texto) {
        var words = ['NO SABE/NO RESPONDE', 'NADA', 'NO SABE', 'NO RESPONDE', 'NINGUNO', 'NO TRABAJABA/NO HACÍA NADA', 'No considera pérdidas'];
        var valido = false;
        for (var i = 0; i < words.length; i++) {
            if (texto.toUpperCase() == words[i]) {
                valido = true;
                break;
            }
        }
        return valido;
    },
    /**
     * Verifica que el texto ingresado no este vacío, tenga la longitud mínima requerida y
     * no contenga caracteres especiales.
     * @param texto          Texto a validar.
     * @param minTxtLenght   Longitud mínima del texto a validar.
     * @return               True cuando es un texto válido.
     **/
    textoCorrecto: function(texto, minTxtLenght) {
        var mensaje = '',
            patron;
        if (texto.length == 0) {
            mensaje = 'longitud';
        } else if (texto.length < minTxtLenght) {
            mensaje = 'txtMinimo';
        } else {
            patron = new RegExp('^[A-ZÑa-zñ ]*$');
            if (!patron.test(texto.toUpperCase())) {
                mensaje = 'caracteresValidos';
            }
        }
        return mensaje;
    },
    /**
     * Valida que el especifique ingresado no este vacío, tenga la longitud mínima requerida y
     * no contenga caracteres especiales.
     * @param texto          Texto del especifique.
     * @param minTxtLenght   Longitud mínima del especifique.
     * @return               True cuando es un especifique válido.
     **/
    especifique: function(texto, minTxtLenght) { //validarEspecifique
        var cred = credential,
            valido = true;
        switch (this.textoCorrecto(texto, minTxtLenght)) {
            case 'longitud':
                credential.setMsg_val_esp('El campo "Especifique" se encuentra vacío, favor de introducir algún valor');
                valido = false;
                break;
            case 'textoIniciaVacio':
                credential.setMsg_val_esp('El campo "Especifique" comienza con uno o varios espacios en blanco, favor de eliminarlos');
                valido = false;
                break;    
            case 'txtMinimo':
                credential.setMsg_val_esp('El campo "Especifique" contiene menos de ' + minTxtLenght + ' caracteres, favor de introducir al menos ' + minTxtLenght + ' caracteres');
                valido = false;
                break;
            case 'caracteresValidos':
                credential.setMsg_val_esp('Verifique que el texto no contenga caracteres especiales, solo son permitidas letras');
                valido = false;
                break;
        }
        return valido;
    },
    /**
     * Valida que la cantidad con letra ingresada no este vacía, tenga la longitud mínima requerida y
     * no contenga caracteres especiales.
     * @param texto          Texto de la cantidad con letra.
     * @param minTxtLenght   Longitud mínima de la cantidad con letra.
     * @return               True cuando la cantidad con letra es válida.
     **/
    cantidadConLetra: function(texto, minTxtLenght) { //validarEspecifique
        var cred = credential,
            valido = true;
        if (!this.palabraCorrecta(texto)) {
            switch (this.textoCorrecto(texto, minTxtLenght)) {
                case 'longitud':
                    credential.setMsg_val_num('Es necesario registrar el valor numérico con letras');
                    valido = false;
                    break;
                case 'textoIniciaVacio':
                    credential.setMsg_val_num('La cantidad con letra comienza con uno o varios espacios en blanco, favor de eliminarlos');
                    valido = false;
                    break;        
                case 'txtMinimo':
                    credential.setMsg_val_num('Favor de introducir al menos ' + minTxtLenght + ' caracteres en el monto');
                    valido = false;
                    break;
                case 'caracteresValidos':
                    credential.setMsg_val_num('Verifique que la cantidad con letra no contenga caracteres especiales, solo son permitidas letras');
                    valido = false;
                    break;
            }
        }
        return valido;
    },
    /**
     * Valida que al menos un check haya sido seleccionado.
     * @param entity       Entidad (tabla) en la que se encuentra el check.
     * @param fieldName    Nombre del campo. Ejemplo : 'check_'. Cuando en los primeros 9 números
     *                     el consecutivo sea 01,02..09 se enviará de la sig. manera: 'check_0'. 
     * @param cantVar      Cantidad de variables consecutivas.
     * @param otras        Otras opciones que no esten contempladas en las variables consecutivas
     *                     separadas por comas. Ejemplo: 97,99.
     * @return             True cuando al menos un check esta seleccionado.
     **/
    alMenosUnCheckSeleccionado: function(entity, fieldName, cantVar) { //validaCheckNulo
        var arg = arguments,
            ent = entity,
            valido = false,
            len = fieldName.length,
            campo = '',
            ponCero,
            argCampo,
            getCampo,
            valor,
            otraOpcion;
        if (arg.length >= 3) {
            ponCero = (fieldName.substring(len - 1, len)) == '0';
            argCampo = ponCero ? (fieldName.substring(0, len - 1)) : fieldName;
            for (var i = 1; i <= cantVar; i++) {
                if (i < 10) {
                    campo = argCampo + (ponCero ? '0' + i : i);
                } else {
                    campo = argCampo + i;
                }
                getCampo = CUESTIONARIO.General.regresaGet(campo);
                valor = ent[getCampo]();
                if (valor != null && valor != '') {
                    valido = true;
                    break;
                }
            }
            if (arg.length > 3 && !valido) {
                for (var j = 3; j < arg.length; j++) {
                    otraOpcion = arg[j];
                    campo = argCampo + otraOpcion;
                    getCampo = CUESTIONARIO.General.regresaGet(campo);
                    valor = ent[getCampo]();
                    if (valor != null && valor != '') {
                        valido = true;
                        break;
                    }
                }
            }
        }
        return valido;
    }
};

CUESTIONARIO.Calculadora = {
    /**
     * Da formato con comas a una cantidad de 1 a 8 dígitos. Ejemplo: 99999999 = 99,999,999
     * 999999 = 999,999    999 = 999
     * @param monto  Valor numérico con o sin comas.
     * @return       Cantidad con formato con comas.
     * */
    agregarComa: function(monto) {
        var temp = monto.replace(/,/g, ''),
            cadena = monto,
            size = temp.length;

        if (size > 3) {
            var index = size == 6 ? 3 : size == 7 ? 1 : size == 8 ? 2 : size - 3;
            var coma = temp.substring(0, index) + ',' + temp.substring(index, size);
            if (size > 6) {
                index = size == 7 ? 5 : 6;
                coma = coma.substring(0, index) + ',' + coma.substring(index, size + 1);
            }
            cadena = coma;
        }
        return cadena;
    }
};
