function test() {
    var resultado;
    if (1 == 1) {
        tr_envipe2023_modulo.setBp1_3c('');
        resultado = true;
    } else {
        resultado = false;
    }
    return resultado;
}

expr.expression:
function test() {
    var valor = tr_envipe2023_modulo.getBp1_5() * 1;
    if (tr_envipe2023_modulo.getBpcod() * 1 == 4 && valor != 2) {
        tr_envipe2023_modulo.setBp1_5('');
        credential.setMsg_val_radio('Si el delito es ROBO DENTRO DE SU CASA (O4), debe seleccionar la OPCIÓN 2');
    } else if (tr_envipe2023_modulo.getBpcod() * 1 == 5 && valor == 2) {
        tr_envipe2023_modulo.setBp1_5('');
        credential.setMsg_val_radio('Con código del delito 05 no puede seleccionar la opción 2');
    } else {
        credential.setMsg_val_radio('Debe seleccionar un código');
    }
    return;
}

validation.validate:
function test() {
    var valor = tr_envipe2023_modulo.getBp1_6(),
        cod = tr_envipe2023_modulo.getBpcod() * 1,
        valido;
    if ((cod == 5 || cod == 11 || cod == 12 || cod == 13 || cod == 14) && valor == 2) {
        tr_envipe2023_modulo.setBp1_6('1');
        //credential.setMsg_val_radio('Con código del delito ' + tr_envipe2020_modulo.getBpcod() + ' solo puede seleccionar la opción 1');
        valido = false;
    } else {
        valido = true;
    }
    return valido;
}

