function test() {
    if (tr_envipe2023_modulo.getBp1_34().length < 7) {
        tr_envipe2023_modulo.setBp1_34(tr_envipe2023_modulo.getBp1_34() + '9');
        tr_envipe2023_modulo.setBp1_34_monto(CUESTIONARIO.Calculadora.agregarComa(tr_envipe2023_modulo.getBp1_34()));
    }

    if (tr_envipe2023_modulo.getBp1_34().length == 7 & tr_envipe2023_modulo.getBp1_34() == '9999999') {
