function test() {
    var monto = tr_envipe2023_modulo.getBp1_34();
    if (monto == '0000000' || monto == '9999999') {
        tr_envipe2023_modulo.setBp1_34('');
        tr_envipe2023_modulo.setBp1_34e('');
        tr_envipe2023_modulo.setBp1_34_monto('');
        tr_envipe2023_modulo.setBp1_34_radio('');
        runtime__.skip('salto110202749'); //Deshabilita Especifique
    } else {
        tr_envipe2023_modulo.setBp1_34_monto(tr_envipe2023_modulo.getBp1_34_monto().substring(0, tr_envipe2023_modulo.getBp1_34_monto().length - 1));
        tr_envipe2023_modulo.setBp1_34(tr_envipe2023_modulo.getBp1_34_monto().replace(/,/g, ''));
        tr_envipe2023_modulo.setBp1_34_monto(CUESTIONARIO.Calculadora.agregarComa(tr_envipe2023_modulo.getBp1_34()));
    }

}

function test() {
    /*global confirm*/
    if (tr_envipe2023_modulo.getBp1_34().length >= 1 && tr_envipe2023_modulo.getBp1_34().length < 7) {
        tr_envipe2023_modulo.setBp1_34(tr_envipe2023_modulo.getBp1_34() + '0');
        tr_envipe2023_modulo.setBp1_34_monto(CUESTIONARIO.Calculadora.agregarComa(tr_envipe2023_modulo.getBp1_34()));
    } else {
        if (tr_envipe2023_modulo.getBp1_34().length < 7) {
            var myAnswer = confirm('¿No considera pérdidas?');
            if (myAnswer) {
                tr_envipe2023_modulo.setBp1_34('0000000');
                tr_envipe2023_modulo.setBp1_34_monto('0,000,000');
                tr_envipe2023_modulo.setBp1_34_radio('00');
                tr_envipe2023_modulo.setBp1_34e('NO CONSIDERA PÉRDIDAS');
                runtime__.skip('salto110202749'); //Deshabilita Especifique
            }
        }
    }
    return false;
}

