        tr_envipe2022_modulo.setBp1_35(tr_envipe2022_modulo.getBp1_35() + '0');
        tr_envipe2022_modulo.setBp1_35_monto(CUESTIONARIO.Calculadora.agregarComa(tr_envipe2022_modulo.getBp1_35()));
    } else {
        if (tr_envipe2022_modulo.getBp1_35().length < 7) {
            var myAnswer = confirm('¿No considera pérdidas?');
