                tr_envipe2022_modulo.setBp1_35('0000000');
                tr_envipe2022_modulo.setBp1_35_monto('0,000,000');
                tr_envipe2022_modulo.setBp1_35_radio('00');
                tr_envipe2022_modulo.setBp1_35e('NO CONSIDERA PÉRDIDAS');
                runtime__.skip('salto123342348'); //Deshabilita Especifique
            }
        }
    }
    return false;
}

