function test() {
    var valido = CUESTIONARIO.Validacion.alMenosUnCheckSeleccionado(tr_envipe2023_modulo, 'bp1_10_', 2, 9);
    var totSeleccionados = 0;
    credential.setMsg_val_check('Debe seleccionar al menos un código');

    if (valido && tr_envipe2023_modulo.getBp1_9() == 1) {
        totSeleccionados = CUESTIONARIO.General.countChSelected(tr_envipe2023_modulo, 'bp1_10_', 2, 9);
        valido = totSeleccionados == 1;
        if (!valido) {
            credential.setMsg_val_check('De acuerdo a la respuesta de la pregunta 1.9 solo puede seleccionar una opción');
        }
    }
    return valido;
}

