function test() {
    /*global runtime__ confirm external*/
    var myAnswer;
    try {
        if (runtime__.hasErrors() == false) {
            myAnswer = confirm('Ha finalizado con el Módulo sobre victimización, ¿Desea continuar?....\n\nTerminar Captura (Aceptar) - Dejar Pendiente (Cancelar)');
            if (!myAnswer) {
                tr_envipe2023_modulo.setStatus__('I');
                runtime__.saveEntity(tr_envipe2023_modulo);
                runtime__.exit();
                return true;
            } else {
                if (credential.getUltimo_delito() * 1 == 1) {
                    external.alert('Hemos terminado la entrevista de la Encuesta Nacional de Victimización y Percepción sobre Seguridad Pública 2022.\nEn nombre del INEGI le agradecemos su participación');
                }
                tr_envipe2023_modulo.setStatus__('C');
                runtime__.saveEntity(tr_envipe2023_modulo);
                runtime__.exit();
                return true;
            }
        } else {
            myAnswer = confirm('Se encontraron errores de validación. \n\n ¿Realmente desea salir del cuestionario?.. \n\nDejar Pendiente(Aceptar) - Salir(Cancelar)');
            if (myAnswer) {
                tr_envipe2023_modulo.setStatus__('I');
                runtime__.saveEntity(tr_envipe2023_modulo);
                runtime__.exit();
                return true;
            } else {
                return true;
            }
        }
    } catch (err) {
        tr_envipe2023_modulo.setStatus__('I');
        runtime__.saveEntity(tr_envipe2023_modulo);
        return false;
    }
    return true;
}

